CommandUIHandler.CommandAction:
javascript:
                         var selectedItems = SP.ListOperation.Selection.getSelectedItems();
                            var selectedItemIds = '';
                            var selectedItemIndex;
                            for (selectedItemIndex in selectedItems){
                              selectedItemIds += '|' + selectedItems[selectedItemIndex].id;
                            }
                        var options = SP.UI.$create_DialogOptions();
                        options.title = '添加图片水印';
                        options.width = 850;
                        options.height = 550;
                        options.url = '/_layouts/Pages/AddWaterImage/AddWaterImage.aspx?listID='+ SP.ListOperation.Selection.getSelectedList()+'&selectedItemIds='+selectedItemIds;
        SP.UI.ModalDialog.showModalDialog(options);

CommandUIHandler.EnabledScript:
javascript:
                            function checkIsEnabled(){
                              // check items selected
                              var selectedItems = SP.ListOperation.Selection.getSelectedItems();
                              var count = CountDictionary(selectedItems);
                              return (count > 0);
                            }; 
                                          
                            checkIsEnabled();
